#!/usr/bin/env nimr
import macros
import sequtils
import strformat
import tables
import common

template doWhile(lbl: untyped, a: typed, b: untyped): untyped =
  while true:
    b
    if not a:
      break lbl

template loopBlock(lbl: untyped, a: typed, b: untyped): untyped =
  block lbl:
    if a != 0.uint8:
      doWhile(lbl, a != 0.uint8):
        b

proc `<-`(a, b: NimNode) =
  case a.kind
  of nnkBlockStmt:
    a[1].add(b)
  of nnkCall:
    a[3].add(b)
  else:
    echo "wtf"

proc genInitialBlock(): NimNode =
  newNimNode(nnkBlockStmt).add(
    newIdentNode("bfProg"),
    newStmtList(
      newNimNode(nnkVarSection).add(
        newNimNode(nnkIdentDefs).add(
          newIdentNode("core"),
          newEmptyNode(),
          newNimNode(nnkCall).add(
            newIdentNode("BFCore")
          )
        )
      )
    )
  )

proc genIncMemory(): NimNode =
  newNimNode(nnkCommand).add(
    newIdentNode("inc"),
    newNimNode(nnkBracketExpr).add(
      newNimNode(nnkDotExpr).add(
        newIdentNode("core"),
        newIdentNode("memory")
      ),
      newNimNode(nnkDotExpr).add(
        newIdentNode("core"),
        newIdentNode("ap")
      )
    )
  )

proc genDecMemory(): NimNode =
  newNimNode(nnkCommand).add(
    newIdentNode("dec"),
    newNimNode(nnkBracketExpr).add(
      newNimNode(nnkDotExpr).add(
        newIdentNode("core"),
        newIdentNode("memory")
      ),
      newNimNode(nnkDotExpr).add(
        newIdentNode("core"),
        newIdentNode("ap")
      )
    )
  )

proc genPrintMemory(): (NimNode, NimNode) =
  result = (
    nnkCommand.newTree(
      nnkDotExpr.newTree(
        newIdentNode("stdout"),
        newIdentNode("write")
      ),
      nnkDotExpr.newTree(
        nnkBracketExpr.newTree(
          nnkDotExpr.newTree(
            newIdentNode("core"),
            newIdentNode("memory")
          ),
          nnkDotExpr.newTree(
            newIdentNode("core"),
            newIdentNode("ap")
          )
        ),
        newIdentNode("char")
      )
    ),
    nnkCall.newTree(
      nnkDotExpr.newTree(
        newIdentNode("stdout"),
        newIdentNode("flushFile")
      )
    )
  )


proc genIncAP(): NimNode =
  newNimNode(nnkCommand).add(
    newIdentNode("inc"),
    newNimNode(nnkDotExpr).add(
        newIdentNode("core"),
        newIdentNode("ap")
      )
  )

proc genDecAP(): NimNode =
  newNimNode(nnkCommand).add(
    newIdentNode("dec"),
    newNimNode(nnkDotExpr).add(
        newIdentNode("core"),
        newIdentNode("ap")
      )
  )

proc genBlock(id: int): NimNode =
  let blockIdent = newIdentNode("b" & $(id))
  result = nnkCall.newTree(
    newIdentNode("loopBlock"),
    blockIdent,
    nnkBracketExpr.newTree(
      nnkDotExpr.newTree(
        newIdentNode("core"),
        newIdentNode("memory")
      ),
      nnkDotExpr.newTree(
        newIdentNode("core"),
        newIdentNode("ap")
      )
    ),
    newStmtList()
  )


proc charToSymbol(c: char): BFSymbol =
  case c
  of '>': BFSymbol(kind: bfsApAdjust, amt: 1)
  of '<': BFSymbol(kind: bfsApAdjust, amt: -1)
  of '+': BFSymbol(kind: bfsMemAdjust, amt: 1)
  of '-': BFSymbol(kind: bfsMemAdjust, amt: -1)
  of '.': BFSymbol(kind: bfsPrint)
  of ',': BFSymbol(kind: bfsRead)
  of '[': BFSymbol(kind: bfsBlock, statements: @[])
  of ']': BFSymbol(kind: bfsBlockEnd)
  else:   BFSymbol(kind: bfsNoOp)

macro compile*(prog: string): untyped =
  result = newStmtList()
  var blockStack = @[genInitialBlock()]

  let instructions = toSeq(prog.strVal.items)
  let symbols = map(instructions, proc(x: char): BFSymbol = charToSymbol(x))
  var blockCount: int = 1
  for inst in instructions:
    case inst
    of '>':
      blockStack[^1] <- genIncAP()
    of '<':
      blockStack[^1] <- genDecAP()
    of '+':
      blockStack[^1] <- genIncMemory()
    of '-':
      blockStack[^1] <- genDecMemory()
    of '.':
      let (print, flush) = genPrintMemory()
      blockStack[^1] <- print
      blockStack[^1] <- flush

    of ',':
      echo "read memory"
    of '[':
      let blk = genBlock(blockCount)
      blockStack[^1] <- blk
      blockStack &= blk
      inc blockCount
    of ']':
      blockStack = blockStack[0.. ^2]

    else: discard
  result.add(blockStack[0])
  #echo result.treeRepr


