#!/usr/bin/env nimr
import macros
import sequtils
import strformat
import tables

var
  HelloWorld = "++++++++[>++++[>++>+++>+++>+<<<<-]>+>+>->>+[<]<-]>>.>---.+++++++..+++.>>.<-.<.+++.------.--------.>>+.>++."

type
  BFCore* = ref object
   memory*: array[1024, uint8]
   pc*, ap*: int


proc interpret*(bf: BFCore, program: string) =
  # Before we start, we need to pre-calculate our jump table
  var
    jumpTbl = initTable[int, int]()
    jumpStk: seq[int] = @[]

  for pc, inst in program:
    case inst
    of '[':
      jumpStk &= pc
    of ']':
      let fjp = jumpStk[^1]
      jumpStk = jumpStk[0.. ^2]
      jumpTbl[pc] = fjp
      jumpTbl[fjp] = pc
    else: discard

  # Clear out our core state
  bf.pc = 0
  bf.ap = 0
  for cell in bf.memory.mitems:
    cell = 0.uint8

  while bf.pc <= program.high:
    case program[bf.pc]
    of '>': inc bf.ap
    of '<': dec bf.ap
    of '+': inc bf.memory[bf.ap]
    of '-': dec bf.memory[bf.ap]
    of '.':
      stdout.write bf.memory[bf.ap].char
      stdout.flushFile()
    of ',':
      var tempMem: array[1, char]
      discard stdin.readChars(tempMem, 0, 1)
      bf.memory[bf.ap] = tempMem[0].uint8
    of '[':
      if bf.memory[bf.ap] == 0.uint8:
        bf.pc = jumpTbl[bf.pc]
    of ']':
      if bf.memory[bf.ap] != 0.uint8:
        bf.pc = jumpTbl[bf.pc]
    else: discard
    inc bf.pc

template doWhile(lbl: untyped, a: typed, b: untyped): untyped =
  while true:
    b
    if not a:
      break lbl

template loopBlock(lbl: untyped, a: typed, b: untyped): untyped =
  block lbl:
    if a != 0.uint8:
      doWhile(lbl, a != 0.uint8):
        b

proc `<-`(a, b: NimNode) =
  case a.kind
  of nnkBlockStmt:
    a[1].add(b)
  of nnkCall:
    a[3].add(b)
  else:
    echo "wtf"

proc genInitialBlock(): NimNode =
  newNimNode(nnkBlockStmt).add(
    newIdentNode("bfProg"),
    newStmtList(
      newNimNode(nnkVarSection).add(
        newNimNode(nnkIdentDefs).add(
          newIdentNode("core"),
          newEmptyNode(),
          newNimNode(nnkCall).add(
            newIdentNode("BFCore")
          )
        )
      )
    )
  )

proc genIncMemory(): NimNode =
  newNimNode(nnkCommand).add(
    newIdentNode("inc"),
    newNimNode(nnkBracketExpr).add(
      newNimNode(nnkDotExpr).add(
        newIdentNode("core"),
        newIdentNode("memory")
      ),
      newNimNode(nnkDotExpr).add(
        newIdentNode("core"),
        newIdentNode("ap")
      )
    )
  )

proc genDecMemory(): NimNode =
  newNimNode(nnkCommand).add(
    newIdentNode("dec"),
    newNimNode(nnkBracketExpr).add(
      newNimNode(nnkDotExpr).add(
        newIdentNode("core"),
        newIdentNode("memory")
      ),
      newNimNode(nnkDotExpr).add(
        newIdentNode("core"),
        newIdentNode("ap")
      )
    )
  )

proc genPrintMemory(): NimNode =
  nnkBlockStmt.newTree(
    newEmptyNode(),
    nnkStmtList.newTree(
      nnkCommand.newTree(
        nnkDotExpr.newTree(
          newIdentNode("stdout"),
          newIdentNode("write")
        ),
        nnkDotExpr.newTree(
          nnkBracketExpr.newTree(
            nnkDotExpr.newTree(
              newIdentNode("core"),
              newIdentNode("memory")
            ),
            nnkDotExpr.newTree(
              newIdentNode("core"),
              newIdentNode("ap")
            )
          ),
          newIdentNode("char")
        )
      ),
      nnkCall.newTree(
        nnkDotExpr.newTree(
          newIdentNode("stdout"),
          newIdentNode("flushFile")
        )
      )
    )
  )

proc genIncAP(): NimNode =
  newNimNode(nnkCommand).add(
    newIdentNode("inc"),
    newNimNode(nnkDotExpr).add(
        newIdentNode("core"),
        newIdentNode("ap")
      )
  )

proc genDecAP(): NimNode =
  newNimNode(nnkCommand).add(
    newIdentNode("dec"),
    newNimNode(nnkDotExpr).add(
        newIdentNode("core"),
        newIdentNode("ap")
      )
  )

proc genBlock(id: int): NimNode =
  let blockIdent = newIdentNode("b" & $(id))
  result = nnkCall.newTree(
    newIdentNode("loopBlock"),
    blockIdent,
    nnkBracketExpr.newTree(
      nnkDotExpr.newTree(
        newIdentNode("core"),
        newIdentNode("memory")
      ),
      nnkDotExpr.newTree(
        newIdentNode("core"),
        newIdentNode("ap")
      )
    ),
    newStmtList()
  )

macro compile*(prog: string): untyped =
  result = newStmtList()
  var blockStack = @[genInitialBlock()]

  let instructions = toSeq(prog.strVal.items)
  var blockCount: int = 1
  for inst in instructions:
    case inst
    of '>':
      blockStack[^1] <- genIncAP()
    of '<':
      blockStack[^1] <- genDecAP()
    of '+':
      blockStack[^1] <- genIncMemory()
    of '-':
      blockStack[^1] <- genDecMemory()
    of '.':
      blockStack[^1] <- genPrintMemory()
    of ',':
      echo "read memory"
    of '[':
      let blk = genBlock(blockCount)
      blockStack[^1] <- blk
      blockStack &= blk
      inc blockCount
    of ']':
      blockStack = blockStack[0.. ^2]

    else: discard
  result.add(blockStack[0])
  #echo result.treeRepr


